mapper.select:
        select *, price*qnt as sum, format(price*qnt, 0) as fmtsum
        from view_cart
        where user_id = #{user_id}
		order by reg_date desc;

select count(*)
from cart
where user_id = #{user_id};

select format(sum(price*qnt), 0) as fmtsum
from view_cart
where user_id = #{user_id};

mapper.update:
update cart
set qnt = qnt + #{qnt}
where store_id = #{store_id} and user_id = #{user_id};

update cart
set qnt = #{qnt}
where cart_id = #{cart_id};

mapper.select:
select count(*)
from cart
where store_id = #{store_id} and user_id = #{user_id};
