mapper.select:
select *
from view_recipe
order by recipe_id desc;

mapper.update:
update recipe
set title = #{title}, description = #{description}, image = #{image}, level = #{level}, mdfy_date=now()
where recipe_id = #{recipe_id}

mapper.select:
select *
from view_recipecomm
order by recipe_id desc;

select count(*) as review_cnt
from comments
where recipe_id = #{recipe_id} and category=2;
