mapper.select:
select *, date_format(reg_date, '%Y년%m월%d일 %T') reg_date, date_format(mdfy_date, '%Y-%m-%d %T') fmtmdfydate
from users
where uid = #{uid};

mapper.update:
update users
set nickname = #{nickname}, phone = #{phone}, address1 = #{address1}, address2 = #{address2}
where uid = #{uid};

update users
set upass = #{upass}
where uid = #{uid};

update users
set image = #{image}
where uid = #{uid};
